import React, { Component } from 'react';
import './Login.css'
import App from './App';
import Register from './Register';

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Register: false,
      Authentication: false,
      EmailValue: '',
      PasswordValue: ''
    };
  }


  navigateRegister = () => {
    this.setState({ Register: true });
  }
  handleEmailChange = (event) => {
    this.setState({ EmailValue: event.target.value });
  };

  handlePasswordChange = (event) => {
    this.setState({ PasswordValue: event.target.value });
  };


  Login = () => {
    fetch('http://localhost:8181/auth/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ email: this.state.EmailValue, password_hash: this.state.PasswordValue })
    })
      .then(res => res.text())
      .then(data => console.log(data));
  }

  render() {
    return (
      <>
        {this.state.Register ? (
          <Register />
        ) : (
          <>
            <h1>Login</h1>
            <div id="loginBox">
              <label>email</label>
              <input onChange={this.handleEmailChange}/>
              <label>password</label>
              <input onChange={this.handlePasswordChange}/>
              <button onClick={this.Login}>Login</button>
              <button onClick={this.navigateRegister}>Register Account</button>
            </div>
            {this.state.Authentication && <App />}
          </>
        )}
      </>
    );
  }
}

export default Login;
